message:
Unneeded mutual recursion detected in these type declarations. It's recommended to rewrite 'class_sign', 'params', 'args', 'fild_sign', 'method_sign', 'constructor_sign', 'class_member' as follows:
type class_sign =
  {
  cl_modif: access_modifier option ;
  cl_id: ident ;
  parent: ident option ;
  cl_mems: class_member list }
type params =
  | Params of expr list [@@deriving ((show { with_path = false }), eq)]
type args =
  | Args of expr list [@@deriving ((show { with_path = false }), eq)]
type fild_sign =
  {
  f_modif: fild_modifier option ;
  f_type: var_type ;
  f_id: ident ;
  f_val: expr option }
type method_sign =
  {
  m_modif: method_modifier option ;
  m_type: meth_type ;
  m_id: ident ;
  m_args: args }
type constructor_sign =
  {
  con_modif: access_modifier option ;
  con_id: ident ;
  con_args: args ;
  base_params: params option }
type class_member =
  | Fild of fild_sign 
  | Main of method_sign * expr 
  | Method of method_sign * expr 
  | Constructor of constructor_sign * expr
